// Prepare products for batch upload
const data = $json;
const products = $node["5. Route Logic"].json.products;

if (!products || products.length === 0) {
  console.log('‚ùå No products to upload');
  return [{ json: { error: 'No products' } }];
}

console.log(`\nüîÑ PREPARING ${products.length} PRODUCTS FOR QDRANT`);

// Process in batches of 100
const BATCH_SIZE = 100;
const batches = [];

for (let i = 0; i < products.length; i += BATCH_SIZE) {
  const batch = products.slice(i, i + BATCH_SIZE);
  const points = batch.map((product, idx) => {
    // Create search text
    const textParts = [];
    if (product.name) textParts.push(product.name);
    if (product.productNumber) textParts.push(product.productNumber);
    if (product.description) textParts.push(product.description);
    if (product.manufacturer?.name) textParts.push(product.manufacturer.name);

    return {
      id: i + idx + 1, // Numeric ID starting from 1
      payload: {
        product_id: product.id,
        name: product.name || product.productNumber || 'Unknown',
        sku: product.productNumber || '',
        price: product.price?.gross || 0,
        description: product.description || '',
        search_text: textParts.join(' '),
        active: product.active !== false
      }
    };
  });

  batches.push({
    batchNumber: Math.floor(i / BATCH_SIZE) + 1,
    points: points
  });
}

console.log(`üì¶ Created ${batches.length} batches of ${BATCH_SIZE} products each`);
console.log('üìù Sample point:', JSON.stringify(batches[0].points[0], null, 2));

// For now, upload first batch
const firstBatch = batches[0];

return [{ json: {
  points: firstBatch.points,
  totalBatches: batches.length,
  currentBatch: 1,
  totalProducts: products.length
} }];

