  } }];
}

const pageProducts = response.data || [];
const apiTotal = response.total || 0;
const currentPage = response.page || state.page;
const limit = response.limit || state.limit;
const allProducts = [...state.allProducts, ...pageProducts];

console.log(`üìÑ Page ${currentPage}: +${pageProducts.length} products`);
console.log(`üìä Total collected: ${allProducts.length}`);

// Check if should continue
let shouldContinue = false;
if (currentPage === 1 && apiTotal === limit) {
  shouldContinue = pageProducts.length === limit;
  console.log('‚ö†Ô∏è  Using count-based pagination');
} else {
  const totalPages = Math.ceil(apiTotal / limit);
  shouldContinue = currentPage < totalPages && pageProducts.length > 0;
}

if (shouldContinue) {
  console.log('‚úÖ Fetching next page...');
  return [{ json: { 
    token: state.token, 
    page: currentPage + 1, 
    limit: limit, 
    allProducts: allProducts,
    continueLoop: true,
    finished: false
  } }];
} else {
  console.log('üèÅ All products fetched!');
