// Transform products to Qdrant format
const data = $json;
const products = data.products;

if (!products || products.length === 0) {
  console.log('‚ùå No products to upload');
  return [{ json: { error: 'No products' } }];
}

console.log(`\nüîÑ TRANSFORMING ${products.length} PRODUCTS FOR QDRANT`);

// Generate simple hash-based vector for demo
// In production, use real embeddings (OpenAI, Cohere, etc.)
function generateVector(text) {
  const vec = [];
  const seed = text || 'default';
  for (let i = 0; i < 384; i++) {
    const hash = (seed.charCodeAt(i % seed.length) * (i + 1)) % 1000;
    vec.push((hash / 1000) * 2 - 1);
  }
  return vec;
}

// Transform each product for Qdrant
const qdrantItems = products.map((product, idx) => {
  const searchText = [
    product.name,
    product.productNumber,
    product.description,
    product.manufacturer?.name
  ].filter(Boolean).join(' ');

  return {
    json: {
      id: String(idx + 1), // Qdrant expects string IDs
      vector: generateVector(searchText),
      payload: {
        product_id: product.id,
        name: product.name || product.productNumber || 'Unknown',
        sku: product.productNumber || '',
        price: product.price?.gross || 0,
        description: product.description || '',
        manufacturer: product.manufacturer?.name || '',
        search_text: searchText,
        active: product.active !== false,
        synced_at: new Date().toISOString()
      }
    }
  };
});

console.log(`üì¶ Transformed ${qdrantItems.length} products`);
