// Transform products for Document Loader format
const data = $json;
const products = data.products;

if (!products || products.length === 0) {
  console.log('‚ùå No products to upload');
  return [];
}

console.log(`\nüîÑ TRANSFORMING ${products.length} PRODUCTS FOR DOCUMENT LOADER`);

// Transform each product to the format expected by Document Loader
const documents = products.map((product, idx) => {
  // Create comprehensive text for embedding
  const textParts = [];
  if (product.name) textParts.push(`Product: ${product.name}`);
  if (product.productNumber) textParts.push(`SKU: ${product.productNumber}`);
  if (product.description) textParts.push(`Description: ${product.description}`);
  if (product.price?.gross) textParts.push(`Price: ‚Ç¨${product.price.gross}`);
  if (product.manufacturer?.name) textParts.push(`Brand: ${product.manufacturer.name}`);

  const pageContent = textParts.join('. ');

  // Return in the format expected by Document Loader
  return {
    pageContent: pageContent,
    metadata: {
      product_id: product.id,
      name: product.name || product.productNumber || 'Unknown',
      sku: product.productNumber || '',
      price: product.price?.gross || 0,
      description: product.description || '',
      manufacturer: product.manufacturer?.name || '',
      active: product.active !== false,
      source: 'shopware-held-de',
      synced_at: new Date().toISOString()
    }
  };
});

console.log(`üì¶ Transformed ${documents.length} documents`);
console.log('üìù Sample document:', {
  pageContent: documents[0].pageContent.substring(0, 100) + '...',
  metadata: documents[0].metadata
});

// Return as a single item containing all documents
return [{ json: { documents } }];

