const response = $json;
if (!response.access_token) throw new Error('OAuth failed');
console.log('‚úÖ OAuth success');

// Initialize pagination state
return [{ json: { 
  token: response.access_token, 
  page: 1, 
  limit: 50,
  allProducts: [],
  continueLoop: true
} }];

// Main pagination loop - fetch all products
const startState = $json;
let currentState = { ...startState };
const allProducts = [];
let detectedIssue = null;

console.log('üöÄ Starting complete product fetch...');

while (currentState.continueLoop && currentState.page <= 50) { // Safety limit
  try {
    console.log(`üìÑ Fetching page ${currentState.page}...`);

    // Note: This is pseudocode - n8n Code nodes can't make HTTP requests
    // We'll use a different approach below
    break;
  } catch (error) {
    console.log('‚ùå Error in loop:', error.message);
    break;
  }
}

// Since we can't loop with HTTP requests in Code node, 
// we'll return state to continue with HTTP Request node
return [{ json: currentState }];

// Smart pagination with single-output routing
const response = $json;
const state = $node["Loop Controller"].json;

if (response.error || !response.data) {
  console.log('‚ùå Error, finishing:', response.error);
  return [{ json: {
    success: false,
    error: response.error,
    totalProducts: state.allProducts.length,
    products: state.allProducts,
    continueLoop: false,
    finished: true
  } }];
}

const pageProducts = response.data || [];
const apiTotal = response.total || 0;
const currentPage = response.page || state.page;
const limit = response.limit || state.limit;
const allProducts = [...state.allProducts, ...pageProducts];

console.log(`üìÑ Page ${currentPage}: +${pageProducts.length} products (API total: ${apiTotal})`);
console.log(`üìä Collected so far: ${allProducts.length}`);

// SMART DETECTION: Check if API is returning bogus totals
let shouldContinue = false;
let detectedIssue = state.detectedIssue;

if (currentPage === 1) {
  if (apiTotal === limit) {
    detectedIssue = 'API_RETURNS_LIMIT_AS_TOTAL';
    console.log('‚ö†Ô∏è  DETECTED: API returns limit as total - using product count logic');
  }
}

if (detectedIssue === 'API_RETURNS_LIMIT_AS_TOTAL') {
  shouldContinue = pageProducts.length === limit;
  console.log(`üîß Count-based logic: got ${pageProducts.length}/${limit}, continue: ${shouldContinue}`);
} else {
  const calculatedTotalPages = Math.ceil(apiTotal / limit);
  shouldContinue = currentPage < calculatedTotalPages && pageProducts.length > 0;
  console.log(`üìê API logic: page ${currentPage}/${calculatedTotalPages}, continue: ${shouldContinue}`);
}

if (shouldContinue) {
  console.log('‚úÖ Continuing to next page...');
  return [{ json: { 
    token: state.token, 
    page: currentPage + 1, 
    limit: limit, 
    allProducts: allProducts,
    detectedIssue: detectedIssue,
    continueLoop: true,
    finished: false
  } }];
} else {
  console.log('üèÅ Pagination complete!');
  return [{ json: {
    success: true,
    totalProducts: allProducts.length,
    estimatedTotal: Math.max(apiTotal, allProducts.length),
    apiReportedTotal: apiTotal,
    detectedIssue: detectedIssue,
    products: allProducts,
    continueLoop: false,
    finished: true
  } }];
}

// Route based on whether we should continue
const data = $json;

if (data.finished) {
  console.log('üéØ Routing to completion...');
  return [{ json: data }]; // Route to transform
} else if (data.continueLoop) {
  console.log('üîÑ Routing to next page...');
  // We need to go back to fetch more - this creates the loop
  return [{ json: data }]; // This will route back to fetch
} else {
  console.log('‚ùì Unexpected state');
  return [{ json: { ...data, finished: true } }];
}

// Transform products for Qdrant vectorization
const data = $json;
console.log('\nüîÑ PREPARING FOR QDRANT UPLOAD');
console.log('=========================================');
console.log(`üìä Products to process: ${data.totalProducts}`);

if (!data.products || data.products.length === 0) {
  console.log('‚ùå No products to upload');
  return [{ json: { success: false, error: 'No products found' } }];
}

// Transform each product for AI chatbot context
const vectorizedProducts = data.products.map(product => {
  const textParts = [];

  if (product.name) textParts.push(`Product: ${product.name}`);
  if (product.productNumber) textParts.push(`SKU: ${product.productNumber}`);
  if (product.description) textParts.push(`Description: ${product.description}`);
  if (product.price && product.price.gross) textParts.push(`Price: ‚Ç¨${product.price.gross}`);
  if (product.manufacturer && product.manufacturer.name) textParts.push(`Brand: ${product.manufacturer.name}`);

  const fullText = textParts.join('. ');

  return {
    id: product.id,
    vector: null,
    payload: {
      productId: product.id,
      name: product.name || product.productNumber,
      productNumber: product.productNumber,
      description: product.description,
      price: product.price,
      active: product.active,
      fullText: fullText,
      syncDate: new Date().toISOString(),
      source: 'shopware-held-de'
    }
  };
});

console.log(`üîÑ Transformed ${vectorizedProducts.length} products for Qdrant`);

return [{ json: { 
  success: true,
  totalProducts: data.totalProducts,
  vectorizedProducts: vectorizedProducts.slice(0, 100), // First 100 for demo
  batchSize: Math.min(100, vectorizedProducts.length),
  detectedIssue: data.detectedIssue
} }];

const uploadResult = $json;
const transformData = $node["Transform for Qdrant"].json;

console.log('\nüéâ SHOPWARE TO QDRANT SYNC COMPLETE!');
console.log('==========================================');
console.log(`üìä Products synced: ${transformData.totalProducts}`);
console.log(`üîÑ Batch uploaded: ${transformData.batchSize}`);
console.log(`üì° Qdrant response status:`, uploadResult.status || 'Success');

if (transformData.detectedIssue) {
  console.log(`‚ö†Ô∏è  API Issue handled: ${transformData.detectedIssue}`);
}

console.log('\n‚úÖ AI Chatbot ready with fresh Shopware data!');
return [{ json: { 
  syncComplete: true,
  productsSynced: transformData.batchSize,
  totalProducts: transformData.totalProducts,
  qdrantCollection: 'shopware_products',
  timestamp: new Date().toISOString()
} }];
