// Main pagination loop - JavaScript only
const { token, page, limit, allProducts } = $json;
let currentPage = page;
let products = [...allProducts];
let hasMore = true;

console.log(`ðŸš€ Starting pagination from page ${currentPage}`);

while (hasMore && currentPage <= 50) { // Safety limit
  console.log(`ðŸ“„ Fetching page ${currentPage}...`);

  // Fetch current page
  const response = await fetch(`https://shop.held.de/api/product?page=${currentPage}&limit=${limit}`, {
    headers: {
      'Authorization': `Bearer ${token}`,
      'Accept': 'application/json'
    }
  });

  if (!response.ok) {
    console.log(`âŒ HTTP Error: ${response.status}`);
    break;
  }

  const data = await response.json();

  if (data.error || !data.data) {
    console.log(`âŒ API Error:`, data.error);
    break;
  }

  const pageProducts = data.data || [];
  const total = data.total || 0;
  const totalPages = Math.ceil(total / limit);

  products = [...products, ...pageProducts];

  console.log(`ðŸ“Š Page ${currentPage}/${totalPages}: +${pageProducts.length} products (${products.length}/${total} total)`);

  // Check if we have more pages
  hasMore = currentPage < totalPages && pageProducts.length > 0;
  currentPage++;

  // Rate limiting
  if (hasMore) {
    console.log('â³ Rate limiting...');
    await new Promise(resolve => setTimeout(resolve, 1000));
  }
}

console.log(`ðŸŽ‰ Pagination complete! Total products: ${products.length}`);

return [{ 
  json: { 
    success: true,
    totalProducts: products.length,
    products: products.slice(0, 10), // First 10 for display
    allProducts: products
  } 
}];

