// Sync ALL products with JavaScript pagination
const response = $json;

if (!response.access_token) {
  throw new Error('OAuth failed: ' + (response.error || 'No token'));
}

const token = response.access_token;
const limit = 25;
let currentPage = 1;
let allProducts = [];
let hasMore = true;

console.log('ðŸš€ Starting complete Held shop sync...');

while (hasMore && currentPage <= 100) { // Safety limit
  console.log(`ðŸ“„ Fetching page ${currentPage} (limit: ${limit})...`);

  try {
    const url = `https://shop.held.de/api/product?page=${currentPage}&limit=${limit}`;
    const apiResponse = await fetch(url, {
      method: 'GET',
      headers: {
        'Authorization': `Bearer ${token}`,
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      }
    });

    if (!apiResponse.ok) {
      console.log(`âŒ HTTP ${apiResponse.status}: ${apiResponse.statusText}`);
      break;
    }

    const data = await apiResponse.json();

    if (data.error) {
      console.log('âŒ API Error:', data.error);
      break;
    }

    if (!data.data || !Array.isArray(data.data)) {
      console.log('âŒ No data array in response');
      break;
    }

    const pageProducts = data.data;
    const total = data.total || 0;
    const totalPages = Math.ceil(total / limit);

    allProducts = [...allProducts, ...pageProducts];

    console.log(`âœ… Page ${currentPage}/${totalPages}: +${pageProducts.length} products`);
    console.log(`ðŸ“Š Progress: ${allProducts.length}/${total} total products`);

    // Check pagination
    if (pageProducts.length === 0 || currentPage >= totalPages) {
      hasMore = false;
      console.log('ðŸ Reached end of data');
    } else {
      currentPage++;
      console.log('â³ Rate limiting (1s)...');
      await new Promise(resolve => setTimeout(resolve, 1000));
    }

  } catch (error) {
    console.log('âŒ Request failed:', error.message);
    break;
  }
}

console.log('\nðŸŽ‰ SYNC COMPLETE!');
console.log(`ðŸ“Š Total products fetched: ${allProducts.length}`);
console.log(`ðŸ“ˆ Pages processed: ${currentPage - 1}`);

// Sample products for verification
const sampleProducts = allProducts.slice(0, 5).map(p => ({
  id: p.id,
  name: p.name || 'No name',
  active: p.active
}));

console.log('ðŸ“¦ Sample products:', JSON.stringify(sampleProducts, null, 2));

return [{
  json: {
    success: true,
    totalProducts: allProducts.length,
    pagesProcessed: currentPage - 1,
    sampleProducts: sampleProducts,
    timestamp: new Date().toISOString()
  }
}];
